mapper.select:
select im_i_image_info.id, image_name, image_path, image_md5, image_type,image_height, image_width, im_i_image_info.create_time, im_i_image_info.update_time from im_i_image_info
, im_i_user_image_info where im_i_user_image_info.user_id = #{userId} and im_i_image_info.id = im_i_user_image_info.image_info_id
limit ${pageSize*(currentPage-1)},#{pageSize}

mapper.update:
update im_i_image_info
set image_md5 = #{imageMd5,jdbcType=VARCHAR}
where image_name = #{imageName,jdbcType=VARCHAR}

mapper.select:
select image_path
from im_i_image_info
where id = #{id,jdbcType=NUMERIC}

select count(*)
from im_i_image_info
where image_md5 = #{imageMd5,jdbcType=VARCHAR}

select count(*)
from im_i_user_image_info
where user_id = #{userId}
  and image_info_id = #{image_info_id}

select small_img_id
from im_i_origin_small_relation
where origin_img_id = #{originId}
